def predict(amount, years):
    final = []
    myclient = pymongo.MongoClient("mongodb://3.20.235.122:27017/")
    mydb = myclient["411projectnew"]
    if mydb.authenticate('superadmin', "411project", source="admin"):
        mycol = mydb["Stock Price"]
        for stock in allstocks:
            myquery = {"Meta Data.2Symbol": stock}
            if (len(list(mycol.find(myquery))) == 0):
                faulty_stocks.append(stock)
                continue
            mydoc = list(mycol.find(myquery))[0]['Monthly Time Series']
            df = pd.DataFrame.from_dict(mydoc)
            df = df.transpose()
            df = df[::-1]
            if (float(df['4close'][-1]) > amount):
                continue
            month_count = np.array(list(range(df.shape[0]))).reshape(-1,1)
            to_predict = np.array(list(range(month_count[-1][0], month_count[-1][0] + 12 * years))).reshape(-1,1)
            model = LinearRegression()
            model.fit(month_count, df['4close'])
            result = model.predict(to_predict)
            if (result[-1] < float(df['4close'][-1])):
                continue
            price[stock] = float(df['4close'][-1])
            min_range = float(df['4close'][-1]) - min(result)
            growth_range = float(df['4close'][-1]) - result[-1]
            high_to_low_count = 0
            for i in range(len(result) - 1):
                if (result[i] > result[i + 1]):
                    high_to_low_count += 1
            final.append((stock, growth_range, min_range, high_to_low_count))
        final = sorted(final, key = lambda x: x[1], reverse = True)
        final = sorted(final, key = lambda x: x[2])
        final = sorted(final, key = lambda x: x[3])
        print(faulty_stocks)
        if (len(final) == 1):
            stock_amount = amount // price[final[0][0]]
            return [final[0][0], stock_amount]
        elif (len(final) == 2):
            stock_amount_1 = amount // price[final[0][0]]
            stock_amount_2 = amount // price[final[1][0]]
            return [final[0][0], stock_amount_1, final[1][0], stock_amount_2]
        elif (len(final) >= 3):
            stock_amount_1 = amount // price[final[0][0]]
            stock_amount_2 = amount // price[final[1][0]]
            stock_amount_3 = amount // price[final[2][0]]
            return [final[0][0], stock_amount_1, final[1][0], stock_amount_2, final[2][0], stock_amount_3]